```solidity
pragma solidity 0.6.12;

contract TokenContract {
    using SafeMath for uint256;

    string public symbol;
    string public name;
    uint8 public decimals;
    uint256 public _totalsupply;
    address payable private owner;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;

    constructor(string memory _symbol, string memory _name, uint8 _decimals, uint256 _supply) public {
        symbol = _symbol;
        name = _name;
        decimals = _decimals;
        _totalsupply = _supply;
        owner = msg.sender;
        balanceof[msg.sender] = _supply;
    }

    function approve(address _to, uint256 _value) public {
        allowance[msg.sender][_to] = _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] = balanceof[msg.sender].sub(_value);
        require(balanceof[_to] + _value >= balanceof[_to]);
        balanceof[_to] = balanceof[_to].add(_value);
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(balanceof[_from] >= _value);
        balanceof[_from] = balanceof[_from].sub(_value);
        require(allowance[_from][msg.sender] >= _value);
        allowance[_from][msg.sender] = allowance[_from][msg.sender].sub(_value);
        require(balanceof[_to] + _value >= balanceof[_to]);
        balanceof[_to] = balanceof[_to].add(_value);
    }

    function sellOne() public {
        require(balanceof[msg.sender] >= 1);
        balanceof[msg.sender] = balanceof[msg.sender].sub(1);
        require(balanceof[address(this)] + 1 >= balanceof[address(this)]);
        balanceof[address(this)] = balanceof[address(this)].add(1);
        uint256 value = ((((_totalsupply-balanceof[address(this)])*0x5af3107a4000)+0x16345785d8a0000)-0x5af3107a4000)-(((((_totalsupply-balanceof[address(this)])*0x5af3107a4000)+0x16345785d8a0000)-0x5af3107a4000)/10);
        msg.sender.transfer(value);
        owner.transfer(value/10);
    }

    function buyOne() public payable {
        uint256 price = (((_totalsupply-balanceof[address(this)])*0x5af3107a4000)+0x16345785d8a0000)+((((_totalsupply-balanceof[address(this)])*0x5af3107a4000)+0x16345785d8a0000)/10);
        require(msg.value == price);
        require(balanceof[address(this)] >= 1);
        balanceof[address(this)] = balanceof[address(this)].sub(1);
        require(balanceof[msg.sender] + 1 >= balanceof[msg.sender]);
        balanceof[msg.sender] = balanceof[msg.sender].add(1);
        owner.transfer(price/10);
    }
}

// SafeMath library
library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        return a - b;
    }
}
```

This contract has the following functionality:
- `approve()` function to allow a certain address to spend a specified amount of tokens on behalf of the caller.
- `transfer()` function to transfer a certain amount of tokens from the caller to a specified address.
- `transferFrom()` function to transfer a certain amount of tokens from a specified address to another specified address.
- `sellOne()` function to decrease the balance of the caller by one and increase the balance of the contract by one, then transferring a calculated amount of ether to the caller and the owner.
- `buyOne()` function to increase the balance of the caller by one and decrease the balance of the contract by one if the ether received equals to the calculated price, then transferring a calculated amount of ether to the owner.
