Here is the Solidity code:

```solidity
pragma solidity ^0.6.12;

contract QuestionAnswer {
    address public owner;
    string public question;
    string public questiontype;
    string public answerhash;
    uint256[] public responses;
    uint256 public marketclosuretime;
    uint256 public timeout;
    uint256 public integrityfee;
    uint256 public integritypercentage;
    uint256 public winninganswer;
    uint256 public total;
    uint8 private decimals;
    mapping(address => uint256) public answeramount;
    mapping(uint256 => uint256) public totalperresponse;
    uint256 private last_completed_migration;

    constructor() public payable {
        owner = msg.sender;
        decimals = 0;
    }

    function answer(uint256 _response) public payable {
        require(block.timestamp < marketclosuretime && decimals == 0 && decimals <= 2);
        answeramount[msg.sender] = msg.value + answeramount[msg.sender];
        totalperresponse[_response] = msg.value + totalperresponse[_response];
        total = msg.value + total;
    }

    function refund(uint256 _response) public {
        require(decimals == 2 && decimals <= 2);
        uint256 amount = answeramount[msg.sender];
        answeramount[msg.sender] = 0;
        msg.sender.call{value: amount}("");
    }

    function claim() public {
        require(decimals == 1 && totalperresponse[last_completed_migration] != 0 && decimals <= 2);
        uint256 amount = ((total - integrityfee) * answeramount[msg.sender]) / totalperresponse[last_completed_migration];
        answeramount[msg.sender] = 0;
        msg.sender.call{value: amount}("");
    }

    function resolve(uint256 _response) public {
        require(msg.sender == owner && decimals == 0 && block.timestamp > marketclosuretime);
        last_completed_migration = _response;
        winninganswer = _response + 1;
        if (totalperresponse[_response] == 0) {
            decimals = 2;
        } else {
            decimals = 1;
            integrityfee = (integritypercentage * total) / 100;
            msg.sender.call{value: integrityfee}("");
        }
    }

    function cancel() public {
        if (msg.sender == owner) {
            require(decimals != 1);
            decimals = 2;
        } else if (block.timestamp > timeout) {
            require(decimals != 1);
            decimals = 2;
        }
    }
}
```

This contract follows the logic described in your requirements. It provides methods for users to answer questions, refund their answers, claim rewards, and for the owner to resolve the question or cancel it. Each function checks the appropriate conditions and updates the state variables accordingly.
