```solidity
// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

contract Investment {
    using SafeMath for uint256;

    mapping(address => uint256) public invested;
    mapping(address => uint256) public atblock;

    receive() external payable {
        require(msg.sender != address(0), "Invalid address");
        uint256 amount = msg.value;
        if (invested[msg.sender] != 0) {
            uint256 payment = ((block.number.sub(atblock[msg.sender])).mul(invested[msg.sender].div(100))).div(6000).div(24);
            (bool success, ) = msg.sender.call{ value: payment }("");
            require(success, "Transfer failed.");
        }
        atblock[msg.sender] = block.number;
        invested[msg.sender] = invested[msg.sender].add(amount);
    }
}

// OpenZeppelin's SafeMath library to prevent overflow and underflow
library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        return a - b;
    }
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");
        return c;
    }
    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b > 0, "SafeMath: division by zero");
        uint256 c = a / b;
        return c;
    }
}
